        public int GetScoreByBirthDateAndRowData(DateTime birthDate, int rowScore)
        {
            int months = ((DateTime.Now.Year - birthDate.Year) * 12) + DateTime.Now.Month - birthDate.Month;
            return GetScoreByMonthsAgeAndRowData(months, rowScore);
        }

        public int GetScoreByMonthsAgeAndRowData(int months, int rowScore)
        {
            int ageIndex = (months - 72) / 6;
            return this.scoring[rowScore, ageIndex];
        }

        public string GetTexualScore(int finalScore)
        {
            return finalScore < 85 ? "מתחת לנורמה" :
                finalScore <= 90 ? "בטווח הנמוך של הנורמה" :
                finalScore < 110 ? "בנורמה" :
                finalScore <= 115 ? "בטווח הגבוה של הנורמה" :
                finalScore <= 120 ? "מעל הנורמה" :
                finalScore > 120 ? "גבוה מאוד" : "";
        }
